import React, { useEffect, useState } from "react";
import { getProducts, getImages } from "../api/api";
import ProductCard from "../components/ProductCard";

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getProducts();
        const productsWithImages = await Promise.all(
          res.data.map(async (p) => {
            const imgRes = await getImages(p.id);
            return { ...p, link: imgRes.data[0]?.url || "/placeholder.jpg" };
          })
        );
        setProducts(productsWithImages);
      } catch (err) { console.error(err); }
    };
    fetchProducts();
  }, []);

  return (
    <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))", gap: "1rem", padding: "2rem" }}>
      {products.map(product => <ProductCard key={product.id} product={product} />)}
    </div>
  );
}
