import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../contexts/CartContext";

export default function ProductCard({ product }) {
  const { addToCart } = useContext(CartContext);

  // Fallback image local si le lien n'est pas exploitable
  const src =
    product?.link &&
    (product.link.startsWith("http") || product.link.startsWith("/images/"))
      ? product.link
      : "/images/placeholder.jpg";

  const handleError = (e) => {
    // Si l'image casse, on bascule sur le placeholder local
    if (e?.currentTarget) e.currentTarget.src = "/images/placeholder.jpg";
  };

  return (
    <div
      style={{
        border: "1px solid #ddd",
        borderRadius: "8px",
        padding: "1rem",
        textAlign: "center",
        backgroundColor: "#fff",
      }}
    >
      <Link to={`/product/${product.id}`}>
        <img
          src={src}
          alt={product.name}
          style={{ width: "100%", borderRadius: "8px" }}
          onError={handleError}
        />
      </Link>

      <h3 style={{ marginTop: "0.75rem" }}>{product.name}</h3>

      <p style={{ fontWeight: "bold", margin: "0.25rem 0 0.75rem" }}>
        {Number(product.price).toFixed(2)} â‚¬
      </p>

      <button onClick={() => addToCart(product)}>Ajouter au panier</button>
    </div>
  );
}
