import React, { useState, useEffect } from "react";
const COOKIE_KEY = "sneakers_cookie_consent";

export default function CookieBanner() {
  const [visible, setVisible] = useState(false);
  const [consent, setConsent] = useState({ analytics:false, marketing:false, personalization:false });

  useEffect(() => {
    const saved = localStorage.getItem(COOKIE_KEY);
    if (!saved) setVisible(true);
    else setConsent(JSON.parse(saved));
  }, []);

  const acceptAll = () => {
    const all = { analytics:true, marketing:true, personalization:true };
    localStorage.setItem(COOKIE_KEY, JSON.stringify(all));
    setConsent(all); setVisible(false);
  };
  const save = () => { localStorage.setItem(COOKIE_KEY, JSON.stringify(consent)); setVisible(false); };

  if (!visible) return null;

  return (
    <div role="dialog" aria-live="polite" style={{ position:"fixed", bottom:0, left:0, right:0, background:"#111", color:"#fff", padding:"1rem", zIndex:999 }}>
      <h3>Nous utilisons des cookies üç™</h3>
      <p>Essentiels, analytiques, marketing et personnalisation (d√©sactiv√©s par d√©faut).</p>
      <div style={{ display:"flex", gap:"1rem", flexWrap:"wrap" }}>
        <label><input type="checkbox" checked disabled /> Essentiels</label>
        <label><input type="checkbox" checked={consent.analytics} onChange={e=>setConsent({ ...consent, analytics:e.target.checked })}/> Analytiques</label>
        <label><input type="checkbox" checked={consent.marketing} onChange={e=>setConsent({ ...consent, marketing:e.target.checked })}/> Marketing</label>
        <label><input type="checkbox" checked={consent.personalization} onChange={e=>setConsent({ ...consent, personalization:e.target.checked })}/> Personnalisation</label>
      </div>
      <div style={{ marginTop:"1rem", display:"flex", gap:"1rem" }}>
        <button onClick={acceptAll} style={{ flex:1 }}>Tout accepter ‚úÖ</button>
        <button onClick={save} style={{ flex:1 }}>Enregistrer üíæ</button>
      </div>
    </div>
  );
}
