import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const { cart, subtotal, removeFromCart, updateQuantity, clearCart, checkoutCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  if (!cart.length) return <div style={{ padding: "2rem" }}>Votre panier est vide</div>;

  const handleCheckout = async () => {
    setLoading(true);
    try {
      const order = await checkoutCart();
      alert(`✅ Commande passée ! ID: ${order.id || "1234"}`);
      navigate("/");
    } catch (err) {
      alert("❌ Erreur lors de la commande");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Mon panier</h1>
      {cart.map(item => (
        <div key={item.id} style={{ marginBottom: "1rem", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <div>
            <b>{item.name}</b> – {Number(item.price).toFixed(2)} € × {item.quantity}
            <div>
              <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
              <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
              <button onClick={() => removeFromCart(item.id)}>❌ Supprimer</button>
            </div>
          </div>
        </div>
      ))}
      <h2>Total : {subtotal.toFixed(2)} €</h2>
      <button onClick={clearCart}>Vider le panier</button>
      <button onClick={handleCheckout} disabled={loading}>
        {loading ? "Paiement en cours..." : "Passer commande / Paiement"}
      </button>
    </div>
  );
}
