// src/components/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import { getProducts, createProduct, deleteProduct } from "../api/api";

export default function AdminDashboard() {
  const [products, setProducts] = useState([]);
  const [newName, setNewName] = useState("");
  const [newPrice, setNewPrice] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await getProducts();
    setProducts(res.data);
  };

  const handleAdd = async () => {
    await createProduct({ name: newName, price: Number(newPrice) });
    setNewName(""); setNewPrice("");
    fetchProducts();
  };

  const handleDelete = async (id) => {
    await deleteProduct(id);
    fetchProducts();
  };

  return (
    <div style={{ maxWidth: 800, margin: "2rem auto", padding: 20, backgroundColor: "#fff", borderRadius: 10 }}>
      <h1>Admin Dashboard</h1>
      <div>
        <input value={newName} onChange={e => setNewName(e.target.value)} placeholder="Nom produit" />
        <input value={newPrice} onChange={e => setNewPrice(e.target.value)} placeholder="Prix" type="number" />
        <button onClick={handleAdd}>Ajouter produit</button>
      </div>
      <ul>
        {products.map(p => (
          <li key={p.id}>
            {p.name} - {Number(p.price).toFixed(2)} â‚¬
            <button onClick={() => handleDelete(p.id)}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
