// src/pages/login.jsx
import { useState } from "react";
import { login as apiLogin, register as apiRegister } from "../api/api";
import { TextField, Button, Typography } from "@mui/material";

export default function Login({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [isRegister, setIsRegister] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = isRegister ? { email, password, first_name: firstName, last_name: lastName } : { email, password };
      const res = isRegister ? await apiRegister(data) : await apiLogin(data);

      if (res.data.token) {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("role", res.data.user.role);
        onLogin(res.data.user);
      } else alert("Identifiants invalides");
    } catch (err) {
      alert(err.response?.data?.message || "Erreur serveur");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ padding: 20, maxWidth: 400, margin: "2rem auto", backgroundColor: "#fff", borderRadius: 10 }}>
      <Typography variant="h5">{isRegister ? "Inscription" : "Connexion"}</Typography>
      {isRegister && (
        <>
          <TextField label="Prénom" value={firstName} onChange={e => setFirstName(e.target.value)} fullWidth margin="normal" />
          <TextField label="Nom" value={lastName} onChange={e => setLastName(e.target.value)} fullWidth margin="normal" />
        </>
      )}
      <TextField type="email" label="Email" value={email} onChange={e => setEmail(e.target.value)} fullWidth margin="normal" />
      <TextField type="password" label="Mot de passe" value={password} onChange={e => setPassword(e.target.value)} fullWidth margin="normal" />
      <Button type="submit" variant="contained" fullWidth sx={{ mt: 2 }}>
        {isRegister ? "S'inscrire" : "Se connecter"}
      </Button>
      <Typography variant="body2" sx={{ mt: 1, cursor: "pointer", color: "blue" }} onClick={() => setIsRegister(!isRegister)}>
        {isRegister ? "Déjà un compte ? Se connecter" : "Pas de compte ? S'inscrire"}
      </Typography>
    </form>
  );
}
