import { useState } from "react";
import { login, setToken } from "../api/api";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await login({ email, password });
      setToken(data.token);
      localStorage.setItem("token", data.token);
      localStorage.setItem("role", data.user.role);
      localStorage.setItem("email", data.user.email);
      window.location.href = "/";
    } catch (err) {
      alert(err.response?.data?.message || "Erreur login");
    }
  };

  return (
    <form onSubmit={submit} style={{ padding: 20, maxWidth: 360 }}>
      <h3>Connexion</h3>
      <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)} style={{ display:"block", width:"100%", margin:"8px 0" }}/>
      <input type="password" placeholder="Mot de passe" value={password} onChange={(e)=>setPassword(e.target.value)} style={{ display:"block", width:"100%", margin:"8px 0" }}/>
      <button>Se connecter</button>
    </form>
  );
}
