import { useEffect, useState } from "react";
import { getProducts, createProduct, updateProduct, deleteProduct, listOrders, setOrderStatus } from "../api/api";

export default function AdminPage(){
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [form, setForm] = useState({ name:"", price:"", category:"hommes" });

  const load = async () => {
    const [p, o] = await Promise.all([getProducts(), listOrders()]);
    setProducts(p.data);
    setOrders(o.data);
  };

  useEffect(()=>{ load(); },[]);

  const submit = async (e) => {
    e.preventDefault();
    const variants = [{ sku:`${form.name.replace(/\W+/g,"").toUpperCase()}-42`, size:"42", stock:5, price:Number(form.price) }];
    await createProduct({ ...form, price:Number(form.price), variants });
    setForm({ name:"", price:"", category:"hommes" });
    await load();
  };

  const patch = async (p) => {
    await updateProduct(p.id, { ...p, price:Number(p.price) });
    await load();
  };

  const remove = async (id) => {
    await deleteProduct(id);
    await load();
  };

  const updateOrder = async (id, status) => {
    await setOrderStatus(id, status);
    setOrders(s => s.map(o => o.id===id ? { ...o, status } : o));
  };

  return (
    <div style={{ padding:20 }}>
      <h2>Admin — Produits</h2>
      <form onSubmit={submit} style={{ display:"flex", gap:8, flexWrap:"wrap", margin:"8px 0" }}>
        <input placeholder="Nom" value={form.name} onChange={e=>setForm(f=>({ ...f, name:e.target.value }))}/>
        <input type="number" step="0.01" placeholder="Prix" value={form.price} onChange={e=>setForm(f=>({ ...f, price:e.target.value }))}/>
        <select value={form.category} onChange={e=>setForm(f=>({ ...f, category:e.target.value }))}>
          <option>hommes</option><option>femmes</option><option>enfants</option><option>sport</option><option>ville</option>
        </select>
        <button>Créer</button>
      </form>

      <ul>
        {products.slice(0,50).map(p=>(
          <li key={p.id} style={{ margin:"6px 0" }}>
            <input value={p.name} onChange={e=>setProducts(ps=>ps.map(x=>x.id===p.id?{...x,name:e.target.value}:x))} style={{ width:220 }}/>
            <input type="number" step="0.01" value={p.price} onChange={e=>setProducts(ps=>ps.map(x=>x.id===p.id?{...x,price:e.target.value}:x))} style={{ width:100, marginLeft:6 }}/>
            <select value={p.category} onChange={e=>setProducts(ps=>ps.map(x=>x.id===p.id?{...x,category:e.target.value}:x))} style={{ marginLeft:6 }}>
              <option>hommes</option><option>femmes</option><option>enfants</option><option>sport</option><option>ville</option>
            </select>
            <button onClick={()=>patch(p)} style={{ marginLeft:6 }}>Enregistrer</button>
            <button onClick={()=>remove(p.id)} style={{ marginLeft:6, color:"crimson" }}>Supprimer</button>
          </li>
        ))}
      </ul>

      <h2 style={{ marginTop:24 }}>Admin — Commandes</h2>
      <ul>
        {orders.map(o=>(
          <li key={o.id} style={{ margin:"6px 0" }}>
            #{o.id} — {o.status} — {o.total}€
            <select value={o.status} onChange={e=>updateOrder(o.id, e.target.value)} style={{ marginLeft:8 }}>
              <option value="paid">paid</option>
              <option value="processing">processing</option>
              <option value="shipped">shipped</option>
              <option value="delivered">delivered</option>
              <option value="cancelled">cancelled</option>
            </select>
          </li>
        ))}
      </ul>
    </div>
  );
}
