// src/pages/Home.jsx
import React, { useEffect, useState } from "react";
import { getProducts, getImages } from "../api/api";
import { Link } from "react-router-dom";

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getProducts();
        const prods = await Promise.all(res.data.map(async (p) => {
          const imgRes = await getImages(p.id);
          return {
            ...p,
            price: Number(p.price), // s'assurer que c'est un number
            image: imgRes.data[0]?.url || "/placeholder.jpg",
          };
        }));
        setProducts(prods);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProducts();
  }, []);

  return (
    <div style={{ maxWidth: 1200, margin: "2rem auto", display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "2rem" }}>
      {products.map(p => (
        <div key={p.id} className="product-card" style={{ transition: "transform 0.3s", cursor: "pointer" }}>
          <Link to={`/product/${p.id}`}>
            <img src={p.image} alt={p.name} style={{ width: "100%", height: 220, objectFit: "cover", borderRadius: 15 }} />
          </Link>
          <div className="product-card-content" style={{ padding: "10px" }}>
            <h2>{p.name}</h2>
            <p style={{ fontWeight: "bold" }}>{p.price.toFixed(2)} â‚¬</p>
          </div>
        </div>
      ))}
    </div>
  );
}
