// src/components/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import { getProducts, createProduct, deleteProduct } from "../api/api";

export default function AdminDashboard() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: "", price: "", description: "" });

  const fetchProducts = async () => {
    const res = await getProducts();
    setProducts(res.data.map(p => ({ ...p, price: Number(p.price) })));
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleCreate = async () => {
    await createProduct({ ...newProduct, price: Number(newProduct.price) });
    setNewProduct({ name: "", price: "", description: "" });
    fetchProducts();
  };

  const handleDelete = async (id) => {
    await deleteProduct(id);
    fetchProducts();
  };

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>

      <div>
        <h2>Ajouter un produit</h2>
        <input placeholder="Nom" value={newProduct.name} onChange={(e) => setNewProduct({...newProduct, name: e.target.value})} />
        <input placeholder="Prix" type="number" value={newProduct.price} onChange={(e) => setNewProduct({...newProduct, price: e.target.value})} />
        <input placeholder="Description" value={newProduct.description} onChange={(e) => setNewProduct({...newProduct, description: e.target.value})} />
        <button onClick={handleCreate}>Créer</button>
      </div>

      <div>
        <h2>Produits existants</h2>
        {products.map((p) => (
          <div key={p.id} style={{ marginBottom: "1rem", borderBottom: "1px solid #ccc", paddingBottom: "0.5rem" }}>
            <strong>{p.name}</strong> - {p.price.toFixed(2)} €
            <p>{p.description}</p>
            <button onClick={() => handleDelete(p.id)}>Supprimer</button>
          </div>
        ))}
      </div>
    </div>
  );
}
