import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";

export default function CartPage() {
  const { cart, subtotal, removeFromCart, updateQuantity, clearCart, checkoutCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  if (!cart.length) return <div style={{ padding: 20 }}>Votre panier est vide</div>;

  const handleCheckout = async () => {
    setLoading(true);
    try {
      const order = await checkoutCart();
      setMessage(`✅ Commande réussie ! ID: ${order.id}`);
    } catch {
      setMessage("❌ Erreur lors de la commande");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Mon panier</h1>
      <ul>
        {cart.map(item => (
          <li key={item.id} style={{ marginBottom: 10 }}>
            <b>{item.name}</b> – {item.price.toFixed(2)} € × {item.quantity}
            <div>
              <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
              <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
              <button onClick={() => removeFromCart(item.id)}>❌ Supprimer</button>
            </div>
          </li>
        ))}
      </ul>
      <h2>Total : {subtotal.toFixed(2)} €</h2>
      <button onClick={clearCart}>Vider le panier</button>
      <button onClick={handleCheckout} disabled={loading}>
        {loading ? "Paiement en cours..." : "Passer commande / Paiement"}
      </button>
      {message && <p>{message}</p>}
    </div>
  );
}
